import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Menu.css';
import logoImage from './logo.png';

const Menu = () => {
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const [menuHeight, setMenuHeight] = useState('0px');
  const [scrollPosition, setScrollPosition] = useState(window.scrollY);

  const handleScroll = () => {
    setScrollPosition(window.scrollY);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const toggleMobileMenu = (event) => {
    setShowMobileMenu(!showMobileMenu);
    setMenuHeight(showMobileMenu ? '0px' : '1000px');
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  };

  return (
    <div>
      <Link to="/">
        <img src={logoImage} alt="Логотип" className="menu-logo" style={{ transform: `translateY(-${scrollPosition}px)` }} />
      </Link>
      <div className={`hamburger-menu ${showMobileMenu ? 'active' : ''}`} onClick={toggleMobileMenu} style={{ transform: `translateY(-${scrollPosition}px)` }}>
        <div className="line"></div>
        <div className="line"></div>
        <div className="line"></div>
      </div>
      <nav className={`menu-container ${showMobileMenu ? 'active' : ''}`} style={{ height: menuHeight }}>
        <ul>
          <li><Link to="/" onClick={(event) => { toggleMobileMenu(event); scrollToTop(); }} className="menu-link">Главная</Link></li>
          <li><Link to="/real-estate-catalog" onClick={(event) => { toggleMobileMenu(event); scrollToTop(); }} className="menu-link">Новостройки</Link></li>
          <li><Link to="/homepage-catalog" onClick={(event) => { toggleMobileMenu(event); scrollToTop(); }} className="menu-link">Дома от застройщиков</Link></li>
          <li><Link to="/mortgage-program" onClick={(event) => { toggleMobileMenu(event); scrollToTop(); }} className="menu-link">Ипотека</Link></li>
          <li><Link to="/mortgage" onClick={(event) => { toggleMobileMenu(event); scrollToTop(); }} className="menu-link">Калькулятор</Link></li>
        </ul>
      </nav>
    </div>
  );
};

export default Menu;
