import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Menu.css';
import logoImage from './logo.png';

const Menu = () => {
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const [menuPosition, setMenuPosition] = useState({ x: 0, y: 0 }); // Состояние для хранения координат меню
  const [menuHeight, setMenuHeight] = useState('0px'); // Изначально меню скрыто

  const toggleMobileMenu = (event) => { // Добавляем аргумент event
    setShowMobileMenu(!showMobileMenu);
    setMenuPosition({ x: event.clientX, y: event.clientY }); // Установка позиции меню по координатам клика
    setMenuHeight(showMobileMenu ? '0px' : '300px'); // Установка высоты меню при открытии и закрытии
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  };

  return (
    <div>
      <Link to="/">
        <img src={logoImage} alt="Логотип" className="menu-logo" />
      </Link>
      <div className={`hamburger-menu ${showMobileMenu ? 'active' : ''}`} onClick={toggleMobileMenu}>
        <div className="line"></div>
        <div className="line"></div>
        <div className="line"></div>
      </div>
      <nav className={`menu-container ${showMobileMenu ? 'active' : ''}`} style={{ height: menuHeight }}>
        <ul>
          <li><Link to="/" onClick={(event) => { toggleMobileMenu(event); scrollToTop(); }}>Главная</Link></li>
          <li><Link to="/real-estate-catalog" onClick={(event) => { toggleMobileMenu(event); scrollToTop(); }}>Новостройки</Link></li>
          <li><Link to="/homepage-catalog" onClick={(event) => { toggleMobileMenu(event); scrollToTop(); }}>Дома от застройщиков</Link></li>
          <li><Link to="/mortgage-program" onClick={(event) => { toggleMobileMenu(event); scrollToTop(); }}>Ипотека</Link></li>
          <li><Link to="/mortgage" onClick={(event) => { toggleMobileMenu(event); scrollToTop(); }}>Калькулятор</Link></li>
        </ul>
      </nav>
    </div>
  );
};

export default Menu;
