import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Menu.css';
import logoImage from './logo.png';

const Menu = () => {
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const [activeMenuItem, setActiveMenuItem] = useState('');
  const location = useLocation();

  useEffect(() => {
    // При загрузке страницы или изменении URL обновляем активный пункт меню
    setActiveMenuItem(location.pathname);
  }, [location]);

  const toggleMobileMenu = () => {
    setShowMobileMenu(!showMobileMenu);
  };

  const handleMenuItemClick = (path) => {
    setActiveMenuItem(path);
    toggleMobileMenu(); // Закрыть мобильное меню после выбора пункта
  };

  return (
    <div>
      <Link to="/">
        <img src={logoImage} alt="Логотип" className="menu-logo" />
      </Link>
      <div className={`hamburger-menu ${showMobileMenu ? 'active' : ''}`} onClick={toggleMobileMenu}>
        <div className="line"></div>
        <div className="line"></div>
        <div className="line"></div>
      </div>
      <nav className={`menu-container ${showMobileMenu ? 'active' : ''}`}>
        <ul>
          <li>
            <Link to="/" onClick={() => handleMenuItemClick('/')} className={activeMenuItem === '/' ? 'active' : ''}>Главная</Link>
          </li>
          <li>
            <Link to="/real-estate-catalog" onClick={() => handleMenuItemClick('/real-estate-catalog')} className={activeMenuItem === '/real-estate-catalog' ? 'active' : ''}>Новостройки</Link>
          </li>
          <li>
            <Link to="/homepage-catalog" onClick={() => handleMenuItemClick('/homepage-catalog')} className={activeMenuItem === '/homepage-catalog' ? 'active' : ''}>Дома от застройщиков</Link>
          </li>
          <li>
            <Link to="/mortgage-program" onClick={() => handleMenuItemClick('/mortgage-program')} className={activeMenuItem === '/mortgage-program' ? 'active' : ''}>Ипотека</Link>
          </li>
          <li>
            <Link to="/mortgage" onClick={() => handleMenuItemClick('/mortgage')} className={activeMenuItem === '/mortgage' ? 'active' : ''}>Калькулятор</Link>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Menu;
