import React, { useState, useEffect } from 'react';
import { Link, useHistory, useLocation } from 'react-router-dom';
import './Menu.css';
import logoImage from './logo.png';

const menuItems = [
  { path: '/', label: 'Главная' },
  { path: '/real-estate-catalog', label: 'Новостройки' },
  { path: '/homepage-catalog', label: 'Дома от застройщиков' },
  { path: '/mortgage-program', label: 'Ипотека' },
  { path: '/mortgage', label: 'Калькулятор' },
];

const Menu = () => {
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const [activeMenuItem, setActiveMenuItem] = useState('');
  const location = useLocation();
  const history = useHistory();

  useEffect(() => {
    setActiveMenuItem(location.pathname);
  }, [location]);

  const toggleMobileMenu = () => {
    setShowMobileMenu(!showMobileMenu);
  };

  const handleMenuItemClick = (path) => {
    setActiveMenuItem(path);
    toggleMobileMenu();
  };

  const handleLogoClick = () => {
    setActiveMenuItem('/');
    history.push('/');
    toggleMobileMenu();
  };

  return (
    <div>
      <Link to="/" onClick={handleLogoClick}>
        <img src={logoImage} alt="Логотип" className="menu-logo" />
      </Link>
      <div className={`hamburger-menu ${showMobileMenu ? 'active' : ''}`} onClick={toggleMobileMenu}>
        <div className="line"></div>
        <div className="line"></div>
        <div className="line"></div>
      </div>
      <nav className={`menu-container ${showMobileMenu ? 'active' : ''}`}>
        <ul>
          {menuItems.map((menuItem) => (
            <li key={menuItem.path}>
              <Link
                to={menuItem.path}
                onClick={() => handleMenuItemClick(menuItem.path)}
                className={activeMenuItem === menuItem.path ? 'active' : ''}
              >
                {menuItem.label}
              </Link>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
};

export default Menu;
